        return new Twig_Source("{% extends 'base.html.twig' %}

{% block title %}Product index{% endblock %}

{% block body %}



    <div class=\"container mt-5\">
            <h1>Product index</h1>

            <table class=\"table\">
            <thead>
            <tr>
                <th>Id</th>
                <th>Name</th>
                <th>Img</th>
                <th>Description</th>
                <th>Price</th>
                <th>actions</th>
            </tr>
            </thead>
            <tbody>
            {% for product in products %}
                <tr>
                    <td>{{ product.id }}</td>
                    <td>{{ product.name }}</td>
                    <td>{{ product.description }}</td>
                    <td>{{ product.price }}</td>
                    <td>
                        <a href=\"{{ path('product_show', {'id': product.id}) }}\">show</a>
                    </td>
                </tr>
            {% else %}
                <tr>
                    <td colspan=\"6\">no records found</td>
                </tr>
            {% endfor %}
            </tbody>
        </table>
            </div>
        </div>
        
    </div>
{% endblock %}
